import { Component, Input } from '@angular/core';
import { Recipes } from '../../recipes.model';
import { RecipesService } from '../../recipes.service';

@Component({
  selector: 'app-recipes-item',
  templateUrl: './recipes-item.component.html',
  styleUrls: ['./recipes-item.component.scss']
})
export class RecipesItemComponent {
  @Input() recipe:Recipes;
  @Input() index: number;

  constructor(private recipesServices: RecipesService) {}
}
<a class="container card" [routerLink]="[index]" class="card">
    <div class="row">
        <div class="tools">
          <div class="circle">
            <span class="red box"></span>
          </div>
          <div class="circle">
            <span class="yellow box"></span>
          </div>
          <div class="circle">
            <span class="green box"></span>
          </div>
        </div>
    </div>
    <div class="row">
        <div class="card_content col-4">
            <div class="bg-image">
                <img [src]="recipe.imagePath" alt="{{ recipe.name }}" class="img-fluid" />
            </div>
            <div class="pull-left col-8">
                <h4 class="card-title font-weight-bold">{{ recipe.name }}</h4>
                <p class="card-text">{{ recipe.description }}</p>
            </div>
        </div>
    </div>
  </a>
