import { Component, OnDestroy, OnInit } from '@angular/core';
import { Recipes } from '../recipes.model';
import { RecipesService } from '../recipes.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Subscription } from 'rxjs';
@Component({
  selector: 'app-recipes-list',
  templateUrl: './recipes-list.component.html',
  styleUrls: ['./recipes-list.component.scss'],
})
export class RecipesListComponent implements OnInit, OnDestroy {

  recipes:Recipes[];
  private recipeSubscription: Subscription;

  constructor(private recipesServices: RecipesService, private router: Router, private route: ActivatedRoute) {}
  ngOnInit() {
    this.recipesServices.recipesChanged.subscribe((recipe: Recipes[])=>{
      this.recipes = recipe;
    })
    this.recipes = this.recipesServices.getRecipes();
  }

  onNewRecipe(){
    this.router.navigate(['new'], { relativeTo: this.route })
  }

  ngOnDestroy() {
    this.recipeSubscription.unsubscribe();
  }

}
<div class="row">
    <div class="col-xs-12">
        <button class="btn btn-success" (click)="onNewRecipe()">New Recipes</button>
    </div>
</div>
<div class="toast" role="alert" aria-live="assertive" aria-atomic="true">
    <div class="toast-header">
      <img src="..." class="rounded me-2" alt="...">
      <strong class="me-auto">Bootstrap</strong>
      <small>11 mins ago</small>
      <button type="button" class="btn-close" data-bs-dismiss="toast" aria-label="Close"></button>
    </div>
    <div class="toast-body">
      Hello, world! This is a toast message.
    </div>
  </div>
<hr>
<div class="row">
    <div class="col-xs-12">
        <app-recipes-item *ngFor="let recipeEl of recipes; let indice=index" [recipe]="recipeEl" [index]="indice"></app-recipes-item>
    </div>
</div>
